import { useLocation } from "react-router-dom";
import { useState,useEffect } from "react";
import axios from 'axios'
import { useParams } from "react-router-dom"

import './IndividualTrainee.scss'
import CourseDetails3 from "../../components/CourseDetails3/CourseDetails3"


var bg = require("./bg.jpeg")


const IndividualTrainee=()=>{

    const [courses , setCourses] = useState(null)
    
    useEffect(()=>{
        const fetchCourses = async ()=>{
            const response = await fetch ('/courses/popularCourses')
            const json = await response.json()

            console.log(json)

            if (response.ok)
            {
                setCourses(json)
            }
          }

        fetchCourses()
    },[])
    const location = useLocation();
    const path = location.pathname.split("/")[2];
    const [trainee, setTrainee] = useState({});
    //const [courses, setCourses] = useState([]);
    
    const params = useParams()
    const id = params.id
    
  
    useEffect(() => {
      const getTrainee = async () => {
        const res = await axios.get("/individualTrainees/" + path);
        setTrainee(res.data);
        setCourses(res.data.currentCourses)
      };
      
      getTrainee();
    }, [path]);


